import React, { Component } from 'react';
import axios from 'axios';

class Create extends Component {
  constructor(props) {
    super(props);
    this.onChangeFirstName = this.onChangeFirstName.bind(this);
    this.onChangeLastName = this.onChangeLastName.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      First_name: '',
      Last_name: '',
      Last_gst_number:''
    }
  }
  onChangeFirstName(e) {
    this.setState({
      First_name: e.target.value
    });
  }
  onChangeLastName(e) {
    this.setState({
      Last_name: e.target.value
    })  
  }
  onChangeGstNumber(e) {
    this.setState({
      Last_gst_number: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    const obj = {
      First_name: this.state.First_name,
      Last_name: this.state.Last_name,
      Last_gst_number: this.state.Last_gst_number
    };
    axios.post('http://localhost:4000/useradd', obj)
        .then(res => console.log(res.data));
    
    this.setState({
      First_name: '',
      Last_name: '',
      Last_gst_number: ''
    })
  }
 
  render() {
    return (
        <div style={{ marginTop: 10 }}>
            <h3>Add New Last</h3>
            <form onSubmit={this.onSubmit}>
                <div className="form-group">
                    <label>First Name:  </label>
                    <input 
                      type="text" 
                      className="form-control" 
                      value={this.state.First_name}
                      onChange={this.onChangeFirstName}
                      />
                </div>
                <div className="form-group">
                    <label>Last Name: </label>
                    <input type="text" 
                      className="form-control"
                      value={this.state.Last_name}
                      onChange={this.onChangeLastName}
                      />
                </div>
                <div className="form-group">
                    <label>GST Number: </label>
                    <input type="text" 
                      className="form-control"
                      value={this.state.Last_gst_number}
                      onChange={this.onChangeGstNumber}
                      />
                </div>
                <div className="form-group">
                    <input type="submit" value="Register Last" className="btn btn-primary"/>
                </div>
            </form>
        </div>
    )
  }
}
export default Create
